import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { withRouter } from "react-router-dom";

import { userActions } from '../actions';
import { levendrActions } from '../actions';

import { history } from '../helpers';
import { Page } from '../components';

class StartPage extends Page {

  componentDidMount() {
    checkStatus(this.props);
  }

  render() {
    checkStatus(this.props);
    const { checkingStatus, loggingIn, isInitialized } = this.props;
    
    if(checkingStatus || loggingIn) {
      return (
        <div>
          <img style={{marginTop: "50vh", "marginLeft": "50vw"}} src="data:image/gif;base64," />
        </div>
      )
    }
    else {
      return (
        <div>
          <img style={{marginTop: "50vh", "marginLeft": "50vw"}} src="data:image/gif;base64," />
        </div>
      );
    }
  }
}

function checkStatus(props) {
  const { isInitialized, loggedIn, checkingStatus } = props;
      
    if (loggedIn && props.location.pathname === '/start') {
      history.push('/');
    }
    else if(isInitialized === undefined && !checkingStatus) {
      const { dispatch } = props;
      dispatch(levendrActions.checkInitialized());  
    }
    else if(isInitialized === false) {
      history.push('/initialize');
    }
    else if(isInitialized === true) {
      history.push('/login');
    }
}

// function mapDispatchToProps(dispatch) {
